<script setup>
import { ref, computed  } from 'vue'

const props = defineProps({
    modelValue: Boolean
})
const emit = defineEmits(['select', 'close'])

// 더미 배우 목록
const actors = ref([
    { id: 1, name: '하츄핑', profileUrl: 'https://i.namu.wiki/i/QjnMdzJauau5gigMeDYQ5JtCqFADzHTlygU5dl426dAMrJiLhBD0SARL90ks6YAQdqVqZJXi9Z3LoPjk022ALA.webp', description: '뮤지컬 배우' },
    { id: 2, name: '조아핑', profileUrl: 'data:image/jpeg;base64,', description: '연극 경력 5년' },
    { id: 3, name: '말랑핑', profileUrl: 'data:image/jpeg;base64,   ', description: 'TV 드라마 다수 출연' },
])

const keyword = ref('')

const filteredActors = computed(() => {
    if (!keyword.value.trim()) return actors.value
    return actors.value.filter(actor => actor.name.includes(keyword.value.trim()))
})
</script>

<template>
    <div class="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
        <div class="bg-white w-[400px] max-h-[80vh] rounded-lg p-6 overflow-auto">
            <div class="flex justify-between items-center mb-4">
                <h2 class="text-lg font-bold">배우 찾기</h2>
                <button @click="emit('close')" class="text-gray-500 hover:text-black">닫기 ✕</button>
            </div>

            <input v-model="keyword" class="w-full border rounded px-3 py-2 mb-4" placeholder="배우 이름을 입력하세요" />

            <ul class="space-y-3">
                <li v-for="actor in filteredActors" :key="actor.id"
                    class="flex items-center gap-4 p-2 hover:bg-gray-100 rounded cursor-pointer"
                    @click="emit('select', actor)">
                    <img :src="actor.profileUrl" alt="프로필" class="w-12 h-16 object-cover rounded border" />
                    <div>
                        <div class="font-semibold">{{ actor.name }}</div>
                        <div class="text-sm text-gray-600">{{ actor.description }}</div>
                    </div>
                </li>
            </ul>
        </div>
    </div>
</template>
